var gitbook = window.gitbook;
var options = {};

gitbook.events.bind('start', function(e, config) {

	// Save config data
    options = config['header-generator-rmp'] || {};
	
});

gitbook.events.on('page.change', function() {
	

    // Generate html for button and menu
    var html = ''
	+'	<li id="Lifecycle_Guide" class="inactive">'
	+'		<a href="/Lifecycle_Guide/index.html"><i class="fa fa-recycle"> Lifecycle Guide</i></a>'
	+'	</li>'
	+'	<li id="End_User_Guide" class="inactive">'
	+'		<a href="/End_User_Guide/index.html"><i class="fa fa-child"> End User Guide</i></a>'
	+'	</li>'
	+'	<li id="Developer_Guide" class="inactive">'
	+'		<a href="/Developer_Guide/index.html"><i class="fa fa-wrench"> Developer Guide</i></a>'
	+'	</li>'
	+'	<li id="Integration_Guide" class="inactive">'
	+'		<a href="/Integration_Guide/index.html"><i class="fa fa-puzzle-piece"> Integration Guide</i></a>'
	+'	</li>'
	+'	<li id="API_Reference" class="inactive">'
	+'		<a href="/API_Reference/index.html"><i class="fa fa-cogs"> API Reference</i></a>'
	+'	</li>'
	+'	<li id="Training" class="inactive">'
	+'		<a href="/Training/index.html"><i class="fa fa-graduation-cap"> Training</i></a>'
	+'	</li> ';

    // Append generated html to page
	$("#P_topMenu").html(html);
	var str = window.location.pathname.substring(1);
	str = str.substring(0,str.indexOf("/"));
	switch(str) {
		case "Lifecycle_Guide":
			document.getElementById("Lifecycle_Guide").className = "active";
			break;
		case "End_User_Guide":
			document.getElementById("End_User_Guide").className = "active";
			break;
		case "Integration_Guide":
			document.getElementById("Integration_Guide").className = "active";
			break;
		case "API_Reference":
			document.getElementById("API_Reference").className = "active";
			break;		
		case "Training":
			document.getElementById("Training").className = "active";
			break;					
		} 
	
});
